import Square from "./comp/Square";
import "./App.css";
import { useState } from "react";
export default function Board() {
  const [values, setValues] = useState(Array(9).fill(null));
  const [xNext, setxNext] = useState(true);

  function handleClick(i) {
    if (values[i] || calWinner(values)) {
      return;
    }

    if (values[i]) {
      return;
    }
    const nextSq = values.slice();
    if (xNext) {
      nextSq[i] = "x";
    } else {
      nextSq[i] = "0";
    }
    setValues(nextSq);
    setxNext(!xNext);
  }

  const winner = calWinner(values);
  let status;
  if (winner) {
    status = "Winner: " + winner;
  } else {
    status = "Next player: " + (xNext ? "X" : "O");
  }

  return (
    <>
      <div className="status">{status}</div>
      <div className="br">
        <Square value={values[0]} onSqClick={() => handleClick(0)} />
        <Square value={values[1]} onSqClick={() => handleClick(1)} />
        <Square value={values[2]} onSqClick={() => handleClick(2)} />
      </div>
      <div className="br">
        <Square value={values[3]} onSqClick={() => handleClick(3)} />
        <Square value={values[4]} onSqClick={() => handleClick(4)} />
        <Square value={values[5]} onSqClick={() => handleClick(5)} />
      </div>
      <div className="br">
        <Square value={values[6]} onSqClick={() => handleClick(6)} />
        <Square value={values[7]} onSqClick={() => handleClick(7)} />
        <Square value={values[8]} onSqClick={() => handleClick(8)} />
      </div>
    </>
  );
}
function calWinner(values) {
  const lines = [
    [0, 1, 2],
    [3, 4, 5],
    [6, 7, 8],
    [0, 3, 6],
    [1, 4, 7],
    [2, 5, 8],
    [0, 4, 8],
    [2, 4, 6],
  ];

  for (let i = 0; i < lines.length; i++) {
    const [a, b, c] = lines[i];
    if (values[a] && values[a] === values[b] && values[a] === values[c]) {
      return values[a];
    }
  }
  return null;
}
