import React, { useState } from "react";

function Square() {
  const [value, setValue] = useState(Array(9).fill(null));

  function handleClick() {
    setValue("X");
  }

  return (
    <button className="square" onClick={handleClick}>
      {value}
    </button>
  );
}

export default Square;
