import Square from "./comp/Square";
import "./App.css";
import { useState } from "react";
export default function Board() {
  const [values, setValues] = useState(Array(9).fill(null));

  function handleClick(i) {
    const nextSq = values.slice();
    nextSq[i] = "X";
    setValues(nextSq);
  }

  return (
    <>
      <div className="br">
        <Square value={values[0]} onSqClick={handleClick} />
        <Square value={values[1]} onSqClick={handleClick} />
        <Square value={values[2]} onSqClick={handleClick} />
      </div>
      <div className="br">
        <Square value={values[3]} onSqClick={handleClick} />
        <Square value={values[4]} onSqClick={handleClick} />
        <Square value={values[5]} onSqClick={handleClick} />
      </div>
      <div className="br">
        <Square value={values[6]} onSqClick={handleClick} />
        <Square value={values[7]} onSqClick={handleClick} />
        <Square value={values[8]} onSqClick={handleClick} />
      </div>
    </>
  );
}
